<div id="app-id">
  <div id="header-id">
  </div>
<canvas :style={borderWidth:this.borderWidth+"px"} @mouseenter="mouse_enter" @mousemove="mouse_move" @mouseleave="mouse_leave" id="effect-id"></canvas>
</div>


*{
  margin:0;
  padding:0;
}
#app-id{
  position:absolute;
  width:100%;
  height:100%;

  flex-direction:column;
  .ivu-slider::before,.ivu-slider::after{
    display:table;
    content:"";
  }
}
#header-id{
  width:100%;
  padding:10px 40px 0;
  &:after{
    display:block;
    content:" ";
  }

}
#effect-id{
  display:block;
  border-color: #D61827;
  border-style:solid;
}



new Vue({
  el:"#app-id",
  data:{
    canvasDom:null,
    width:0,
    height:0,
    pen:null,
    borderWidth:1,
    color:"",
    data:[],
    enterX:0,
    enterY:0,
    isStart:false,


  },
  methods:{

    mouse_enter($event){
      this.isStart = true;
      this.color = 'hsl('+(Math.random()*360)+',60%,80%)';
      this.pen.fillStyle =  this.color;
      this.enterX = $event.pageX - this.canvasDom.offsetLeft - this.borderWidth;
      this.enterY = $event.pageY - this.canvasDom.offsetTop - this.borderWidth
      if(this.data.length == 0){
        this.start();
      }

    },
    mouse_move($event){
      this.enterX = $event.pageX - this.canvasDom.offsetLeft - this.borderWidth;
      this.enterY = $event.pageY - this.canvasDom.offsetTop - this.borderWidth
    },
   mouse_leave(){
     this.isStart = false;
   },
   start(){

      if(this.isStart){
        this.data.push({

        })
      }
     this.pen.clearRect(0,0,this.width,this.height);
     for(let i=0;i<this.data.length;i++){
          let item = this.data[i];
     }

     this.pen.globalAlpha = 1;
     if(this.isStart || this.data.length > 0){
       requestAnimationFrame(()=>{
         this.start();
       })
     }
     else{
       this.pen.clearRect(0,0,this.width,this.height)
     }
   }

  },
  mounted(){
   this.canvasDom = document.getElementById("effect-id");
   this.headerDom = document.getElementById("header-id");
   this.pen = this.canvasDom.getContext("2d");

   var computed = ()=>{
      let headerDomComputedStyle = window.getComputedStyle(this.headerDom);
      let width = window.innerWidth;

       let height = window.innerHeight - ( parseInt(headerDomComputedStyle.height) || 0)
      this.canvasDom.setAttribute("width",width-2*this.borderWidth)
      this.canvasDom.setAttribute("height",height-2*this.borderWidth)
      this.width = width;
      this.height = height;
    }
   computed();
   window.addEventListener('resize',computed); 


  }
})
